// Combinando todas as bebidas
export const detoxDrinks: DetoxDrink[] = [
  ...underweightDrinks,
  ...extraUnderweightDrinks,
  ...normalDrinks,
  ...extraNormalDrinks,
  ...overweightDrinks,
  ...extraOverweightDrinks,
  ...obeseDrinks,
  ...extraObeseDrinks,
];

// Função para filtrar por categoria
export const getDetoxByCategory = (category: IMCCategory): DetoxDrink[] => {
  return detoxDrinks.filter(drink => drink.category === category);
};

// Função para filtrar por categoria e horário
export const getDetoxByTimeOfDay = (category: IMCCategory, timeOfDay: 'morning' | 'afternoon' | 'night'): DetoxDrink[] => {
  return detoxDrinks.filter(drink => drink.category === category && drink.timeOfDay === timeOfDay);
};
