// Combine all recipes
export const recipes: Recipe[] = [
  ...underweightRecipes,
  ...normalRecipes,
  ...overweightRecipes,
  ...obeseRecipes,
];

// Helper function to get recipes by category and meal time
export const getRecipesByMealTime = (category: IMCCategory, mealTime: 'morning' | 'afternoon' | 'night'): Recipe[] => {
  return recipes.filter(r => r.category === category && r.mealTime === mealTime);
};

// Get category description
export const getCategoryDescription = (category: IMCCategory): string => {
  const descriptions = {
    underweight: 'Receitas calóricas para ganho de peso saudável',
    normal: 'Receitas equilibradas para manutenção do peso',
    overweight: 'Receitas leves para emagrecimento gradual',
    obese: 'Receitas muito leves para perda de peso',
  };
  return descriptions[category] || descriptions.normal;
};
