// Função para filtrar receitas por categoria IMC e horário
export const getRecipesByMealTime = (category: IMCCategory, mealTime: 'morning' | 'afternoon' | 'night' | 'dessert'): Recipe[] => {
  return recipes.filter(recipe => recipe.category === category && recipe.mealTime === mealTime);
};

// Função para obter descrição por categoria
export const getCategoryDescription = (category: IMCCategory): string => {
  const descriptions = {
    underweight: 'Receitas nutritivas para ganho de peso saudável',
    normal: 'Receitas equilibradas para manutenção do peso',
    overweight: 'Receitas leves para auxiliar no emagrecimento',
    obese: 'Receitas muito leves para perda de peso gradual',
  };
  return descriptions[category];
};

// Função para obter receitas recomendadas por IMC
export const getRecommendedRecipes = (imcCategory: IMCCategory): Recipe[] => {
  return recipes.filter(recipe => recipe.category === imcCategory);
};
